import java.sql.*;
import java.util.Scanner;

public class BasicATMSystem {
    private static final String DB_URL = "jdbc:sqlite:atm.db";
    private static Connection conn;
    private static Scanner scanner = new Scanner(System.in);

    public static void main(String[] args) throws SQLException {
        connectToDatabase();
        createAccountTable();

        while (true) {
            System.out.println("Welcome to the ATM!");
            System.out.println("1. Create Account");
            System.out.println("2. Deposit");
            System.out.println("3. Withdraw");
            System.out.println("4. Balance Inquiry");
            System.out.println("5. Fund Transfer");
            System.out.println("6. Exit");

            int choice = scanner.nextInt();
            switch (choice) {
                case 1:
                    createAccount();
                    break;
                case 2:
                    deposit();
                    break;
                case 3:
                    withdraw();
                    break;
                case 4:
                    balanceInquiry();
                    break;
                case 5:
                    fundTransfer();
                    break;
                case 6:
                    System.exit(0);
                default:
                    System.out.println("Invalid option! Please try again.");
            }
        }
    }

    private static void connectToDatabase() {
        try {
            conn = DriverManager.getConnection(DB_URL);
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void createAccountTable() {
        String sql = "CREATE TABLE IF NOT EXISTS accounts ("
                + "id INTEGER PRIMARY KEY AUTOINCREMENT,"
                + "name TEXT NOT NULL,"
                + "balance REAL DEFAULT 0);";

        try (Statement stmt = conn.createStatement()) {
            stmt.execute(sql);
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void createAccount() {
        System.out.print("Enter your name: ");
        scanner.nextLine();
        String name = scanner.nextLine();

        String sql = "INSERT INTO accounts(name) VALUES(?)";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setString(1, name);
            pstmt.executeUpdate();
            System.out.println("Account created successfully!");
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void deposit() {
        System.out.print("Enter account ID: ");
        int id = scanner.nextInt();
        System.out.print("Enter amount to deposit: ");
        double amount = scanner.nextDouble();

        String sql = "UPDATE accounts SET balance = balance + ? WHERE id = ?";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setDouble(1, amount);
            pstmt.setInt(2, id);
            pstmt.executeUpdate();
            System.out.println("Deposit successful!");
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void withdraw() {
        System.out.print("Enter account ID: ");
        int id = scanner.nextInt();
        System.out.print("Enter amount to withdraw: ");
        double amount = scanner.nextDouble();

        String sql = "UPDATE accounts SET balance = balance - ? WHERE id = ?";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setDouble(1, amount);
            pstmt.setInt(2, id);
            pstmt.executeUpdate();
            System.out.println("Withdrawal successful!");
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void balanceInquiry() {
        System.out.print("Enter account ID: ");
        int id = scanner.nextInt();

        String sql = "SELECT name, balance FROM accounts WHERE id = ?";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setInt(1, id);
            ResultSet rs = pstmt.executeQuery();
            if (rs.next()) {
                System.out.println("Name: " + rs.getString("name"));
                System.out.println("Balance: $" + rs.getDouble("balance"));
            } else {
                System.out.println("Account not found!");
            }
        } catch (SQLException e) {
            System.err.println(e.getMessage());
        }
    }

    private static void fundTransfer() {
        System.out.print("Enter source account ID: ");
        int srcId = scanner.nextInt();
        System.out.print("Enter destination account ID: ");
        int destId = scanner.nextInt();
        System.out.print("Enter amount to transfer: ");
        double amount = scanner.nextDouble();

        // Withdraw from the source account
        String withdrawSql = "UPDATE accounts SET balance = balance - ? WHERE id = ?";
        // Deposit into the destination account
        String depositSql = "UPDATE accounts SET balance = balance + ? WHERE id = ?";

        try (PreparedStatement withdrawPstmt = conn.prepareStatement(withdrawSql);
             PreparedStatement depositPstmt = conn.prepareStatement(depositSql)) {

            conn.setAutoCommit(false);

            withdrawPstmt.setDouble(1, amount);
            withdrawPstmt.setInt(2, srcId);
            withdrawPstmt.executeUpdate();

            depositPstmt.setDouble(1, amount);
            depositPstmt.setInt(2, destId);
            depositPstmt.executeUpdate();

            conn.commit();
            System.out.println("Fund transfer successful!");

        } catch (SQLException e) {
            try {
                conn.rollback();
            } catch (SQLException ex) {
                System.err.println(ex.getMessage());
            }
            System.err.println(e.getMessage());
        } finally {
            try {
                conn.setAutoCommit(true);
            } catch (SQLException e) {
                System.err.println(e.getMessage());
            }
        }
    }
}

